INSERT INTO `produk` (`id`, `nama`, `id_kategori`, `deskripsi`, `harga`, `stok`, `gambar`) VALUES
(7, 'SANMOL TABLET 500MG per Strip isi 4 Tablet (per Strip)', 12, 'Sanmol Tablet 500 mg adalah obat yang memiliki kandungan bahan aktif Paracetamol. Paracetamol bekerja dengan cara mengurangi kadar prostaglandin di dalam tubuh, sehingga tanda peradangan seperti demam dan nyeri akan berkurang. Obat ini digunakan untuk mer', 1843, 50, '628b7aa8dfc2c.png'),
(8, 'CURCUMA PLUS EMULSION BLACKURRANT 200ML (per Botol)', 4, 'CURCUMA PLUS EMULSION BLACKURRANT 200 ML Adalah suplemen untuk membantu anak tumbuh dengan baik. Kandungan Cod Liver Oil dan Ekstrak Curcuma dapat meningkatkan daya tahan tubuh dan nafsu makan. Tambahan 7 vitamin yang terkandung di dalamnya mendukung pert', 31541, 20, '628b7b0608ec8.png'),
(9, 'LACTO B SACHET (per Pcs)', 4, 'Lacto B Sachet merupakan probiotik dalam bentuk serbuk yang dapat digunakan untuk memperbaiki fungsi normal dari saluran pencernaan dan juga untuk memperbaiki kondisi seperti diare, konstipasi, dispepsia, diare akibat laktosa, penggunaan antibiotika jangk', 7960, 10, '628b7b78b19ed.jpg'),
